$( document ).on( 'click', '.nav-list li', function ( e ) {
    $( this ).addClass( 'active' ).siblings().removeClass( 'active' );
} );

$( document ).on( 'inview', '.about', function ( event, isInView ) {
    $( this ).addClass( 'active' ).siblings().removeClass( 'active' );
} );


$( document ).on('inview', '#about', function(event, isInView) {
  if (isInView) {
      $( this ).addClass( 'active' ).siblings().removeClass( 'active' );
  } else {
    // element has gone out of viewport
  }
});